SELECT * FROM (SELECT rownum as seq, k.*
FROM (SELECT p.code AS "productCode" , p.name AS "productName" , P.brand_code as "brandCode" , (P.brand_code || '/' || pi.img_origin_name)as "imgPath" , (SELECT name FROM brand WHERE code = p.brand_code) as "brandName" , TO_CHAR(p.price, 'FM999,999,999,999') AS "price" , p.like_count AS "likeCount" , p.color as "color" , ROW_NUMBER() OVER(PARTITION BY p.code ORDER BY PI.SEQ DESC) AS "RN" FROM product p INNER JOIN (SELECT a.product_code , a.img_origin_name , MIN(a.seq) AS SEQ FROM product_image a WHERE a.thumbanil_yn = 'Y' GROUP BY a.product_code, a.img_origin_name ) pi ON p.code = pi.product_code AND p.brand_code = #{brandCode} WHERE 1 = 1
AND color IN
#{item}
ORDER BY p.like_count DESC
) k WHERE 1=1 AND k.RN = 1 ) where 1=1 AND seq >= #{startNum} AND seq <= #{endNum}
